import React, { useEffect, useState } from 'react';
// import { ReactDOM } from 'react-dom';
import Descriptions from './components/Descriptions';

import coldImg from './assets/cold2.jpg';
import { getWeatherData } from './weatherData';
//import hotImg from './assets/hot.jpg';


function App() {

  const [weather, setWeather] = useState(null);

  useEffect(() => {
    const fetchWeatherData = async () => {
      const data = await getWeatherData('paris');
      //console.log(data);
      setWeather(data);
    };

    fetchWeatherData();
  }, [])



  return (
    <div className="app" style={{backgroundImage: `url(${coldImg})`}}>
      <div className='overlay'>

          {/* container renders only if weather is not null      */}
          {
            weather && (
                <div className='container'>
          <div className='section section__inputs'>
            <input type='text' name='city' placeholder='Enter city here...' />
            <button> °F</button>
          </div>

          <div className='section section__temperature'>
            <div className='icon'>
              <h3>{`${weather.name}, ${weather.country}`}</h3>
              <img src={weather.iconURL} alt='weatherIcon'/>
              <h3>{weather.description}</h3>
            </div>
            <div className='temperature'>
              <h1>`${weather.temperature} °C`</h1>
            </div>
          </div>

          {/* bottom description */}
          <Descriptions />
        </div>
            )
          }

      </div>

    </div>
  );
}

export default App;
