import React from 'react';
import { ReactDOM } from 'react-dom';
import coldImg from './assets/cold2.jpg';
//import hotImg from './assets/hot.jpg';


function App() {
  return (
    <div className="app" style={{backgroundImage: `url(${coldImg})`}}>
      <div className='overlay'>
        <div className='container'>
          <div className='section section__inputs'>
            <input type='text' name='city' placeholder='Enter city here...'></input>
          <button> °F</button>
          </div>
          <div className='section section__temperature'>
            <div className='icon'>
              <h3>London, GB</h3>
              <img src='https://openweathermap.org/img/wn/02d@2x.png' alt='weatherIcon'/>
              <h3>Cloudy</h3>
            </div>
            <div className='temperature'>
              <h1>8 °C</h1>
            </div>
          </div>

          {/* bottom description */}
        </div>
      </div>

    </div>
  );
}

export default App;
